    // called once by the factory at time of deployment
    function initialize(address _token0, address _token1, address _ctoken0, address _ctoken1) external {
        require(msg.sender == factory, 'DeBankSwap: FORBIDDEN');
        // sufficient check
        token0 = _token0;
